function getBtypeList($id,$mode){
	$billTypeList=SELECT("bill_type","*","1=1");
	$bTypeList="";
	switch($mode){
		case "text":
			$bTypeList=$id?SELECTA("bill_type","id",$id,"NAME"):"未选择";
		break;
		case "option":
			foreach($billTypeList as $row){
				if($id==$row["id"]){
					$bTypeList.="<option  selected value='".$row["id"]."'>".$row["name"]."</option>";
				}else{
					$bTypeList.="<option value='".$row["id"]."'>".$row["NAME"]."</option>";		
				}
			}
		break;
	}

	return $bTypeList; 
	
}
