function curl($url){
	$json =curl_init();
	curl_setopt($json, CURLOPT_URL, $url);
	curl_setopt($json,CURLOPT_SSL_VERIFYPEER, FALSE);
	curl_setopt($json, CURLOPT_SSL_VERIFYHOST, FALSE);
	curl_setopt($json,CURLOPT_RETURNTRANSFER, 1);
	$json1 = curl_exec($json);
	curl_close($json);
	$bcode=json_decode($json1,true);
	return $bcode;
}

function  SELECT($TAB,$COL,$CONDITION){ 
	global $db;
	$db->clearMessage();
	$db->select($TAB," ".$COL."",$CONDITION);
	$ARR=$db->fetchArray(MYSQL_ASSOC);
	$errorMsg=$db->printMessage();
	if($errorMsg==""){
	//	echo "[sql]SELECT $COL FROM $TAB WHERE $CONDITION [/sql]";
		if(empty($ARR)){
			return $ARR;
		}else{
			return $ARR;
		}
	}else{
		$SQL="[sql]SELECT $COL FROM $TAB  $CONDITION [/sql]";
		return "[ERROR] $SQL $errorMsg [/ERROR]";
	}


		
	
	
}

