function  EXISTA($TABLE,$COL,$VALUE){
	global $db;
	$A=SELECT($TABLE,"*",$COL."='".$VALUE."'");
	if(empty($A)){
		return false;
	}else{
		return true;
	}
}

function  EXISTS($TABLE,$CONDITION){
	global $db;
	$A=SELECT($TABLE,"*"," $CONDITION ");
	if(empty($A)){
		return false;
	}else{
		return true;
	}
} 
