function CONDITION($CONDITION){
	$CONDITION=str_replace("\'","",$CONDITION);
	$CDT=explode("AND",$CONDITION);
	$CDT_COUNT=count($CDT);
	$COUNT=0;
	foreach($CDT as $CDT_S){
		$COUNT++;
		$CDT_S=explode("=",$CDT_S);
		if($COUNT<$CDT_COUNT){
			if(is_numeric($CDT_S[1])){
				$CDT_NEW.=$CDT_S[0]."=".$CDT_S[1]." AND ";
			}else{
				$CDT_NEW.=$CDT_S[0]."='".$CDT_S[1]."' AND ";
			}
		}else{
			if(is_numeric($CDT_S[1])){
				$CDT_NEW.=$CDT_S[0]."=".$CDT_S[1];
			}else{
				$CDT_NEW.=$CDT_S[0]."='".$CDT_S[1]."'";
			}
		}	 
	}
		return $CDT_NEW;
}

